import{store}from"./store.js";import{render}from"./render.js";import{readFile}from"./filereader.js";import{findDuplicateData}from"./util.js";const inputReader=document.getElementById("formFileMultiple"),nextButton=document.getElementById("next"),prevButton=document.getElementById("prev"),init=async e=>{let t;try{t=await readFile(e)}catch(e){alert("Invalid file")}const n=findDuplicateData(t);store.set(n),render(n,0,9)},pagination=(()=>{let e=0,t=9;return n=>{if("next"===n.target.name)e+=10,t+=10;else{if(!(e>0))return void alert("No more items to go back");e-=10,t-=10}const r=store.get();render(r,e,t)}})();inputReader.addEventListener("change",init),nextButton.addEventListener("click",pagination),prevButton.addEventListener("click",pagination);